import React, { useState, useEffect } from "react";
import moment from "moment";
import axios from "axios";
import humid from "./humid.svg"
import temp from "./temp.svg"
import wind_stream from "./wind_stream.svg"
import tempo from "./tempo.svg"


const WeatherApp = () => {
  const [location, setLocation] = useState("");
  const [weatherData, setWeatherData] = useState(null);
  const [loading, setLoading] = useState(false);

  useEffect(() => {
    const fetchWeatherData = async () => {
      if (location) {
        setLoading(true);
        const apiKey = "94769bac42f99a1dd90610f2611e3ae4";
        const apiUrl = `https://api.openweathermap.org/data/2.5/weather?q=${location}&appid=${apiKey}&units=metric`;
        try {
          const response = await axios.get(apiUrl);
          setWeatherData(response.data);
          setLoading(false);
        } catch (error) {
          console.log(error);
          setLoading(false);
        }
      }
    };

    fetchWeatherData();
  }, [location]);

  const handleLocationChange = (event) => {
    setLocation(event.target.value);
  };

  const renderWeatherData = () => {
    if (loading) {
      return <p>Loading weather data...</p>;
    }

    if (!weatherData) {
      return null;
    }

    const weatherDescription = weatherData.weather[0].description;
    const temperature = Math.round(weatherData.main.temp);
    const feelsLikeTemperature = Math.round(weatherData.main.feels_like);
    const humidity = weatherData.main.humidity;
    const windSpeed = weatherData.wind.speed;

    const styl = {
      color : 'red'
    }

    return (
      <div>
        <h2>{weatherDescription}</h2>
        <p>
          <img src= {temp} alt="" className="temp" style={styl}/>
          Temperature: {temperature}°C
        </p>

        <p>
          <img src={tempo} alt=""/>
          Feels like: {feelsLikeTemperature}°C</p>

        <p>
          <img src= {humid} alt=""/>
          Humidity: {humidity}%
        </p>

        <p>
          <img src= {wind_stream} alt=""/>
          Wind Speed: {windSpeed} m/s
        </p>
      </div>
    );
  };

  const renderLocationDropdown = () => {
    return (
      <select value={location} onChange={handleLocationChange}>
        <option value="">Select a location...</option>
        {/* <option value="New York">New York</option>
        <option value="London">London</option>
        <option value="Tokyo">Tokyo</option>
        <option value="Sydney">Sydney</option>
        <option value="Japan">Japan</option>
        <option value="Mumbai">Mumbai</option>
        <option value="Hyderabad">Hyderabad</option> */}
        <option value="Afghanistan">Afghanistan</option>
        <option value="Albania">Albania</option>
        <option value="Algeria">Algeria</option>
        <option value="Argentina">Argentina</option>
        <option value="Australia">Australia</option>
        <option value="Austria">Austria</option>
        <option value="Bangladesh">Bangladesh</option>
        <option value="Belgium">Belgium</option>
        <option value="Bhutan">Bhutan</option>
        <option value="Bolivia">Bolivia</option>
        <option value=""></option>
      </select>
    );
  };

  const renderHeader = () => {
    const date = moment().format("MMMM Do YYYY, h:mm:ss a");

    return (
      <div>
        <h1>Hello user!</h1>
        <p>{date}</p>
      </div>
    );
  };

  return (
    <div>
      {renderHeader()}
      {renderLocationDropdown()}
      {renderWeatherData()}
    </div>
  );
};

export default WeatherApp;


