import React, { useState, useEffect } from "react";
import moment from "moment";
import axios from "axios";

// const 

const WeatherApp = () => {
  const [location, setLocation] = useState("");
  const [weatherData, setWeatherData] = useState(null);
  const [loading, setLoading] = useState(false);

  useEffect(() => {
    const fetchWeatherData = async () => {
      if (location) {
        setLoading(true);
        const apiKey = "94769bac42f99a1dd90610f2611e3ae4";
        const apiUrl = `https://api.openweathermap.org/data/2.5/weather?q=${location}&appid=${apiKey}&units=metric`;
        try {
          const response = await axios.get(apiUrl);
          setWeatherData(response.data);
          setLoading(false);
        } catch (error) {
          console.log(error);
          setLoading(false);
        }
      }
    };

    fetchWeatherData();
  }, [location]);

  const handleLocationChange = (event) => {
    setLocation(event.target.value);
  };

  const renderWeatherData = () => {
    if (loading) {
      return <p>Loading weather data...</p>;
    }

    if (!weatherData) {
      return null;
    }

    const weatherDescription = weatherData.weather[0].description;
    const temperature = Math.round(weatherData.main.temp);
    const feelsLikeTemperature = Math.round(weatherData.main.feels_like);
    const humidity = weatherData.main.humidity;
    const windSpeed = weatherData.wind.speed;

    return (
      <div>
        <h2>{weatherDescription}</h2>
        <p>Temperature: {temperature}°C</p>
        <p>Feels like: {feelsLikeTemperature}°C</p>
        <img src="/Users/arjun/Desktop/Code/React/weather_app/public/images/Humidity.png"></img>
        <p>Humidity: {humidity}%</p>
        <p>Wind Speed: {windSpeed} m/s</p>
      </div>
    );
  };

  const renderLocationDropdown = () => {
    return (
      <select value={location} onChange={handleLocationChange}>
        <option value="">Select a location...</option>
        <option value="New York">New York</option>
        <option value="London">London</option>
        <option value="Tokyo">Tokyo</option>
        <option value="Sydney">Sydney</option>
        <option value="Japan">Japan</option>
        <option value="Mumbai">Mumbai</option>
        <option value="Hyderabad">Hyderabad</option>
      </select>
    );
  };

  const renderHeader = () => {
    const date = moment().format("MMMM Do YYYY, h:mm:ss a");

    return (
      <div>
        <h1>Hello user!</h1>
        <p>{date}</p>
      </div>
    );
  };

  return (
    <div>
      {renderHeader()}
      {renderLocationDropdown()}
      {renderWeatherData()}
    </div>
  );
};

export default WeatherApp;


